$_MODULE['<{gmerchantcenterpro}default-bootstrap>module-tools_class_33de4786ef798eb21fb17e358f2907b9'] = 'tag \"ID\" => indique l\'identifiant du produit. L\'identifiant de chaque produit doit être unique dans votre compte et ne peut pas être réutilisé d\'un flux à l\'autre';
$_MODULE['<{gmerchantcenterpro}default-bootstrap>module-tools_class_535327a84262bb09c699b7d8443aef71'] = 'tag \"TITLE\" => \"TITRE\" : Il s\'agit du nom du produit, qui est obligatoire';
$_MODULE['<{gmerchantcenterpro}default-bootstrap>module-tools_class_105678f76b5b7f580cf7e42e342cfa8a'] = 'tag \"DESCRIPTION\" => indique la description du produit. Comme nous utilisons ce texte pour permettre à l\'utilisateur de trouver votre article, n\'indiquez que des informations pertinentes pour le produit et soyez exhaustif';
$_MODULE['<{gmerchantcenterpro}default-bootstrap>module-tools_class_0f89b407a76c4507108d902cb4cef43b'] = 'tag \"GOOGLE PRODUCT CATEGORY\" => \"CATEGORIE DE PRODUIT GOOGLE\" : indique la catégorie Google du produit proposé, sur la base de la classification des produits Google (correspondance entre vos catégories et les catégories Google à faire dans la section \"Gestion Google\" -> \"Correspondance avec les catégories Google\" du module)';
$_MODULE['<{gmerchantcenterpro}default-bootstrap>module-tools_class_0553038d1985c7647fe480d8f6408135'] = 'tag \"PRODUCT TYPE\" => \"TYPE DE PRODUIT\" : indique la catégorie du produit proposé, d\'après VOTRE propre classification';
$_MODULE['<{gmerchantcenterpro}default-bootstrap>module-tools_class_376652ea6cb436d7d31a89348baa5e78'] = 'tag \"LINK\" => \"LIEN\" : URL pointant directement vers la page de votre produit sur votre site Web. L\'utilisateur est redirigé vers cette URL lorsqu\'il clique sur votre article dans Google Shopping (c\'est la landing page)';
$_MODULE['<{gmerchantcenterpro}default-bootstrap>module-tools_class_21751997886728fb8dee8f8d97d5e301'] = 'tag \"IMAGE LINK\" => \"LIEN DE L\'IMAGE\" : URL de l\'image du produit sur votre site Web. Choisissez la plus grande taille disponible pour vos photos. Google exige au minimum 250x250 px et conseille au moins 400x400 px';
$_MODULE['<{gmerchantcenterpro}default-bootstrap>module-tools_class_1f94aa64616eadbdd5406b922c71f29d'] = 'tag \"CONDITION\" => \"ETAT\" : il indique la condition (ou état) du produit. Seules trois valeurs sont possibles : neuf [new], occasion [used] , reconditionné [refurbished]';
$_MODULE['<{gmerchantcenterpro}default-bootstrap>module-tools_class_8aafe959bbe48eb02154f9dd01c1b987'] = 'tag \"AVAILABILITY\" => \"DISPONIBILITE\" : indique la disponibilité du produit. L\'attribut de disponibilité peut être associé à trois valeurs seulement : en stock [in stock], non disponible [out of stock], disponible en précommande [preorder]';
$_MODULE['<{gmerchantcenterpro}default-bootstrap>module-tools_class_0e27a71d34ae4a363529a379325ec82d'] = 'tag \"PRICE\" => \"PRIX\" : il s\'agit du prix de l\'article. Ce prix doit correspondre au prix le plus visible sur la page de destination.';
$_MODULE['<{gmerchantcenterpro}default-bootstrap>module-tools_class_43016d7329bf2ac3184f865f01d0d0dc'] = 'tag \"GTIN\" =>  code d\'article international GTIN (Global Trade Identification Number) du produit. Sous PrestaShop il est indiqué sous les codes EAN13/JAN (pour l\'Europe et le Japon) ou UPC (pour les Etats Unis)';
$_MODULE['<{gmerchantcenterpro}default-bootstrap>module-tools_class_95c185f2c72bfcda8bb443aae59a5fe1'] = 'tag \"BRAND\" => \"MARQUE\" : il indique la marque du produit. Obligatoire pour tous les produits, sauf pour les livres, les produits multimédias et les biens personnalisés, ou si vous avez déjà défini les attributs : référence fournisseur [mpn] et code EAN13/JAN ou UPC [gtin].';
$_MODULE['<{gmerchantcenterpro}default-bootstrap>module-tools_class_f2dc5d0a47e95dbf13670358baf33777'] = 'tag \"MPN\" =>  indique la référence fournisseur du produit. Il s\'agit d\'un code permettant au fournisseur d\'identifier de façon unique un produit. Plus précisément, la combinaison de la marque et de la référence fournisseur identifie clairement un produit.';
$_MODULE['<{gmerchantcenterpro}default-bootstrap>module-tools_class_7fdc89119c57b600a00faae7a3cb193d'] = 'tag \"ADULT\" => \"ADULTE\" : il désigne les produits réservés uniquement à un public d\'adultes. Ce tag influence l\'endroit où ces produits pourront être vus.';
$_MODULE['<{gmerchantcenterpro}default-bootstrap>module-tools_class_4f8b1378ba0b094ea6017609b24b8b81'] = 'tag \"GENDER\" => \"GENRE\" : il indique le genre (homme, femme, unisexe) des personnes auquelles est destiné le produit. Trois valeurs prédéfinies sont possibles : Homme (male), Femme (female), Unisexe (unisex)';
$_MODULE['<{gmerchantcenterpro}default-bootstrap>module-tools_class_fb3eddd09de2823ec3be475c10efa0a6'] = 'tag \"AGE GROUP\" =>\"TRANCHE D\'AGE\" : il indique la tranche d\'âge pour laquelle le produit est destiné. Cinq valeurs prédéfinies sont possibles : Adultes (adult), Enfants (kids), Tout-petits (toddler), Bébés (infant), Nourrissons (newborn)';
$_MODULE['<{gmerchantcenterpro}default-bootstrap>module-tools_class_828d5792f6e0420f89c85fe36b68ecbe'] = 'tag \"COLOR\" => \"COULEUR\" : il indique la ou les couleurs dominantes du produit';
$_MODULE['<{gmerchantcenterpro}default-bootstrap>module-tools_class_b21faedada81d28249bacaa291bb1f64'] = 'tag \"SIZE\" => \"TAILLE\" : il indique la taille du produit';
$_MODULE['<{gmerchantcenterpro}default-bootstrap>module-tools_class_7529cf9554ca24e52d66ae6acec38417'] = 'tag \"SIZE TYPE\" => \"TYPE DE COUPE\" : il indique le type de coupe du produit (surtout destiné aux vêtements). Cinq valeurs prédéfinies sont possibles : Maternité (maternity), Grande taille Homme (big and tall), Grande taille Femme (plus), Petite taille Femme (petite), Standard (regular)';
$_MODULE['<{gmerchantcenterpro}default-bootstrap>module-tools_class_0b1c668ed78505296decab867eb68939'] = 'tag \"SIZE SYSTEM\" => \"SYSTEME DE TAILLES\" : il indique le système de tailles utilisé pour le produit. Seules les valeurs indiquées dans la configuration du module sont acceptées';
$_MODULE['<{gmerchantcenterpro}default-bootstrap>module-tools_class_803d18ceedf37835dcac0f31abd8b3df'] = 'tag \"MATERIAL\" => \"MATIERE\" : il indique la matière ou le tissu constituant le produit';
$_MODULE['<{gmerchantcenterpro}default-bootstrap>module-tools_class_ba4c31c1b3ae660caa0b73ba195b297c'] = 'tag \"PATTERN\" => \"MOTIF\" : il indique le motif ou l\'imprimé figurant sur le produit';
$_MODULE['<{gmerchantcenterpro}default-bootstrap>module-tools_class_3c8a80bbd741dae60b6ad04a417d4303'] = 'tag \"ITEM GROUP ID\" => \"ID GROUPE\" : il indique l\'ID du groupe d\'articles qui correspondent aux variantes de couleur/matière/motif/taille d\'un MEME produit ';
$_MODULE['<{gmerchantcenterpro}default-bootstrap>module-tools_class_91400488d34b0e6315c4ea1236545131'] = 'tag \"SHIPPING WEIGHT\" => \"POIDS\" : il s\'agit du poids du produit permettant de calculer les frais de port. Si vous avez défini une règle de livraison globale basée sur le poids du colis, cet attribut sert à calculer automatiquement les frais de port du produit';
$_MODULE['<{gmerchantcenterpro}default-bootstrap>module-tools_class_5e1299a0cca324dc27e1b078626a248b'] = 'tag \"SHIPPING\" => \"LIVRAISON\" : il indique les frais de port pour un produit. Utilisez cet attribut lorsque vous devez remplacer les paramètres de livraison que vous avez configurés dans Merchant Center, par exemple pour les produits volumineux ou fragiles';
$_MODULE['<{gmerchantcenterpro}default-bootstrap>module-tools_class_0837abe323d3774a16672202217f38e4'] = 'Les titres ne peuvent pas dépasser 150 caractères selon les pré-requis de Google';
